#include <stdint.h>
#include <stdio.h>
#include "stm32f4xx.h"
#include "system_stm32f4xx.h"
#include "myeeprom.h"
#include "myi2c.h"
#include "myuart.h"

#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

int main(void)
{
	char str[100] ;
	int choice;
	char str2 [100];
	SystemInit();
	UartInit(BAUD_9600);
	EEPROM_Init();
	do
		{
			sprintf(str, "Menu:\n\r1. Write String to EEPROM\n\r2. Read String from EEPROM\n\n\r");
			UartPuts(str);

			sprintf(str, "Select your choice: ");
			UartPuts(str);
			UartGets(str);
			sscanf(str, "%d", &choice);
			switch(choice) {
				case 0:
						sprintf(str, "\n\rThank You\n\r");
						UartPuts(str);
						break;
				case 1:

						sprintf(str, "Enter the string\n\r");
						UartPuts(str);
						UartGets(str);
						sscanf(str, "%s", str2);
						EEPROM_Write(0x0020, (uint8_t*)str2, 32);
						break;

				case 2:
						sprintf(str, "Reading the String: \n\r");
						EEPROM_Read(0x0020, (uint8_t*)str2, 32);
						UartPuts(str2);
						sprintf(str, "\n\r");
						UartPuts(str);
						break;

				default:
						sprintf(str, "Invalid choice\n");
						UartPuts(str);
			}
		}while(choice != 0);

		while(1);
		return 0;
}
